// Pobla selects únicos
function populateSelect(selector, field, data) {
    const select = $(selector);
    select.empty().append('<option value="">Todos</option>');
    [...new Set(data.map(d => d[field]).filter(Boolean))].sort()
        .forEach(val => select.append(`<option value="${val}">${val}</option>`));
}

function applyFilters() {
    let delito = $('#delito').val();
    let factor = $('#factor').val();
    let institucion = $('#institucion').val();
    let estrategia = $('#estrategia').val();
    
    let filtered = originalData.filter(d =>
        (!delito || d.Delito === delito) &&
        (!factor || d.Factor === factor) &&
        (!institucion || d.Institucion === institucion) &&
        (!estrategia || d.Normativas === estrategia)
    );
    tabla.clear().rows.add(filtered).draw();
	
	 actualizarFiltrosActivos(delito, factor, institucion, estrategia);
}

// Función para actualizar los badges de filtros activos
function actualizarFiltrosActivos(delito, factor, institucion, estrategia) {
    const badgesContainer = $('#active-filters-badges');
    badgesContainer.empty();

    if (delito) {
        badgesContainer.append(`<span class="badge bg-primary me-2">Delito: ${delito}</span>`);
    }
    if (factor) {
        badgesContainer.append(`<span class="badge bg-info text-dark me-2">Factor: ${factor}</span>`);
    }
    if (institucion) {
        badgesContainer.append(`<span class="badge bg-success me-2">Territorio: ${institucion}</span>`);
    }
	if (estrategia) {
        badgesContainer.append(`<span class="badge bg-primary me-2">Estrategia PISCC: ${estrategia}</span>`);
    }
	
}

$(document).ready(async function() {
    originalData = await fetchData();
    tabla = $('#tabla').DataTable({
        data: originalData,
        columns: [
            { data: 'Institucion' },
            { data: 'Delito' },
			{ data: 'Factor' },           
            { data: 'Normativas' },
            { data: 'Oferta' }
        ],
        dom: 'Bfrtip',
        buttons: [
            {
                extend: 'excelHtml5',
                text: 'Exportar a Excel',
                className: 'btn btn-success'
            }
        ],
        language: {
            url: '//cdn.datatables.net/plug-ins/1.13.6/i18n/es-ES.json'
        }
    });

    populateSelect('#delito', 'Delito', originalData);
    populateSelect('#factor', 'Factor', originalData);
    populateSelect('#institucion', 'Institucion', originalData);   
	populateSelect('#estrategia', 'Normativas', originalData);   

    $('#delito, #factor, #institucion, #estrategia').on('change', function() {
        applyFilters();
    });

    $('#reset').on('click', function() {
        $('#delito, #factor, #institucion, #estrategia').val('');
        applyFilters();
    });

    $('#searchBox').on('keyup', function() {
        tabla.search(this.value).draw();
    });
});